mapper.select:
		select a.billno "billno",
		(select c.name from area_tab c where c.code = a.acceptzone) "acceptzone",
		a.acceptdw "acceptdw",a.acceptaddr "acceptaddr",a.acceptman "acceptman",
    	a.accepttel "accepttel",to_char(sysdate,'YYYY-MM-DD Hh24-mi-ss') "dyrq",
		(select c.name from hwlb_tab c where c.code = b.hwlb) "hwlb",a.cusno "cusno",a.boxes "boxes",a.volume "volume",
		(select c.peop_name from peop_tab c where c.peop_code = '310100001') "dyr",a.memo "memo"
		from bill_tab a,billfee_tab b
		where a.billno=b.billno
		and b.cus_code in ('200006','200007')
		AND a.lrrq>=#{lrrq_S} 
		AND a.lrrq<=#{lrrq_E} 
		AND a.postnet=#{lrwd}
		AND a.cusno=#{cusno}
		${billno}
